const ADD = 'ADD';

function addMessage(message) {
  return {
    type: ADD,
    message: message
  };
};

function messageReducer(previousState, action) {
  return [...previousState, action.message];
}

const store = {
  state: [],
  getState: () => store.state,
  dispatch: (action) => {
    if(action.type === ADD) {
      store.state = messageReducer(store.state, action);
    }
  }
};
