function findLongestWordLength(str) {
  let words = str.split(" ");
  let num = [];
  for (let i = 0; i < words.length; i++) {
    num.push(words[i].length);
  }

let answer = num.sort(function(a, b) {
  return a-b;
})
.pop();

return answer;

}

findLongestWordLength("The quick brown fox jumped over the lazy dog");
