function addTogether() {
  let args = Array.from(arguments);
  return args.some((num) => typeof num !== "number") ? 
    undefined :
    args.length > 1 ?
      args.reduce((a, b) => a += b, 0) :
      (num) => typeof num === "number" ? 
        num + args[0] :
        undefined;
}

console.log(addTogether(2, 3));
