function pairElement(str) {
  let dna = [];
  let arr = str.split("");

for (let i = 0; i < arr.length; i++) {
  switch (arr[i]) {
    case "C": dna.push(["C", "G"]);
    break;
    case "A": dna.push(["A", "T"]);
    break;
    case "T": dna.push(["T", "A"]);
    break;
    case "G": dna.push(["G", "C"]);
    break;
  }

}
  return dna;
}

