function pairElement(str) {
  let gene = [];

  str.split("").map((letter) => {
    switch (letter) {
      case "A": 
        gene.push(["A", "T"]);
        break;
      case "T": 
        gene.push(["T", "A"]);
        break;
      case "G": 
        gene.push(["G", "C"]);
        break;
      case "C": 
        gene.push(["C", "G"]);
        break;
    }
  });
  return gene;
}

