function orbitalPeriod(arr) {
  const GM = 398600.4418;
  const EARTH_RADIUS = 6367.4447;


  arr.forEach(function(thing) {
    let alt = Math.round(2 * Math.PI * Math.sqrt(Math.pow(EARTH_RADIUS + thing.avgAlt, 3) / GM));
    delete thing.avgAlt;
    thing.orbitalPeriod = alt;
  });

return arr;

  }

