function translatePigLatin(str) {
  let vowel = /[aeiou]/gi;

  if (str.match(vowel) === null) {
    return str + "ay";
  } else if (str[0].match(vowel)) {
    return str + "way";
  } else {
    let consonants = /([^aeiou]+)(\w+)/;
    return str.replace(consonants, "$2$1ay");
  }
}

