function myReplace(str, before, after) {
  let lc = /[a-z]/;

  if (lc.test(before[0]) && lc.test(after[0]) || 
      !lc.test(before[0]) && !lc.test(after[0])) {
    return str.replace(before, after);
  } else if (lc.test(before[0]) && !lc.test(after[0])) {
    after.split("");
    let low = after[0].toLowerCase() + after.slice(1);
    return str.replace(before, low);
  } else {
    let upper = after[0].toUpperCase() + after.slice(1);
    return str.replace(before, upper);
  }

}

console.log(myReplace("A quick brown fox jumped over the lazy dog", "jumped", "Leaped"));
