function smallestCommons(arr) {
  let range = [];

  for(let i = Math.max(...arr);i >= Math.min(...arr); i--) {
    range.push(i);
  }


  let lcm = range[0];
  for (let i = 1; i < range.length; i++) {
    let GCD = gcd(lcm, range[i]);
    lcm = (lcm * range[i]) / GCD;
  }

return lcm;

function gcd(x, y) {
  if (y === 0) {
    return x;
  } else {
    return gcd(y, x % y);
  }
}

}


