function sumPrimes(num) {

function isPrime(el) {
  for (let i = 2; i <= el; i++) {
    if (el % i === 0 && el != i) {
      return false;
    }
  }
  return true;
}

if (num === 1) {
  return 0;
}

  if(isPrime(num) === false) {
    return sumPrimes(num - 1);
  } 

  if(isPrime(num) === true) {
    return num + sumPrimes(num - 1);
  }

}

