function rot13(str) {
  let uni = str.split("");
  let bl = [];

for (let i = 0; i < uni.length; i++) {
    bl.push(uni[i].charCodeAt());

}
  return bl.map((num) => 
        num >= 65 && num <= 77 ? num + 13 : 
        num >= 78 && num <= 90 ? num - 13 :
        num)
        .map((num) => String.fromCharCode(num)).join("");


}

